output_sparql:
prefix bag: <http://bag.basisregistraties.overheid.nl/def/bag#>
prefix brt: <http://brt.basisregistraties.overheid.nl/def/top10nl#>
prefix geo: <http://www.opengis.net/ont/geosparql#>
prefix kad: <https://data.kkg.kadaster.nl/kad/model/def/>
prefix rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#>
prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#>
prefix skos: <http://www.w3.org/2004/02/skos/core#>
prefix sor: <https://data.kkg.kadaster.nl/sor/model/def/>
prefix wbk: <https://data.labs.kadaster.nl/cbs/wbk/vocab/>
select distinct ?punt ?puntLabel ?vlak {
  ?gemeente a wbk:Gemeente;
            rdfs:label ?gemeenteNaam.
  ?wijk a wbk:Wijk;
        rdfs:label ?wijkNaam;
        geo:sfWithin ?gemeente.
  ?buurt a wbk:Buurt;
         geo:sfWithin ?wijk;
         rdfs:label ?buurtNaam.
  ?gebouw a sor:Gebouw;
         geo:sfWithin ?buurt .
  ?gebouwzone a sor:Gebouwzone;
              (kad:gebouwtype/skos:prefLabel) ?typeGebouw;
               sor:hoortBij ?verblijfsobject.
  ?verblijfsobject a sor:Verblijfsobject;
                   geo:hasGeometry ?puntGeometerie;
                   sor:hoofdadres ?nummeraanduiding;
                   sor:maaktDeelUitVan ?gebouw;
                   sor:oppervlakte ?oppervlakte.
  ?puntGeometerie geo:asWKT ?punt;
          rdfs:isDefinedBy <http://bag.basisregistraties.overheid.nl/def/bag#>.
  ?nummeraanduiding
    a sor:Nummeraanduiding;
    sor:huisnummer ?huisnummer;
    sor:ligtAan ?straat;
    sor:postcode ?postcode.
  ?straat skos:prefLabel ?straatNaam .
  BIND(CONCAT(STR(?straatNaam), " ", STR(?huisnummer), ", ", STR(?postcode)) AS ?adres)
  ?gebouw a sor:Gebouw;
          geo:hasGeometry ?geometrie .
  ?geometrie geo:asWKT ?vlak;
          rdfs:isDefinedBy <http://bag.basisregistraties.overheid.nl/def/bag#>.
  bind(strdt(concat(
        '<h4>',str(?typeGebouw),'</h4>',
        '<p>Address: <a href="',str(?verblijfsobject),'" target="_blank">',str(?adres),'</a>',
        '<br>Oppervlakte: ',replace(str(?oppervlakte),'[0-9](?=(?:[0-9]{3})+(?![0-9]))','$0,'),'</p>'),rdf:HTML) as ?puntLabel)
}
limit 100
