output_sparql:
prefix geo: <http://www.opengis.net/ont/geosparql#>
prefix rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#>
prefix skos: <http://www.w3.org/2004/02/skos/core#>
prefix sor: <https://data.kkg.kadaster.nl/sor/model/def/>
select distinct ?punt ?puntLabel ?vlak {
  {
    select * {
      ?openbareRuimte sor:ligtIn ?woonplaats.
      ?nummeraanduiding
        sor:huisnummer ?huisnummer;
        sor:ligtAan ?openbareRuimte;
        sor:postcode ?postcode.
      ?verblijfsobject
        sor:hoofdadres ?nummeraanduiding;
        sor:maaktDeelUitVan ?gebouw.
    }
    limit 250
  }
  ?gebouw
    geo:hasGeometry/geo:asWKT ?vlak;
    sor:oorspronkelijkBouwjaar ?bouwjaar;
    sor:status/skos:prefLabel ?gebouwStatusLabel.
  optional { ?nummeraanduiding sor:huisletter ?huisletter. }
  optional { ?nummeraanduiding sor:huisnummertoevoeging ?huisnummertoevoeging. }
  ?openbareRuimte skos:prefLabel ?openbareRuimteNaam.
  ?verblijfsobject
    geo:hasGeometry/geo:asWKT ?punt;
    sor:oppervlakte ?oppervlakte;
    sor:status/skos:prefLabel ?verblijfsobjectStatusLabel.
  ?woonplaats skos:prefLabel ?woonplaatsNaam.
  bind(strdt(concat(
'<h4><a href="',str(?openbareRuimte),'" target="_blank">',str(?openbareRuimteNaam),'</a> ',str(?huisnummer),
  coalesce(?huisletter,''),
  if(bound(?huisnummertoevoeging),concat('-',str(?huisnummertoevoeging)),''),',',
  '<br>',
  ?postcode,' <a href="',str(?woonplaats),'" target="_blank">',str(?woonplaatsNaam),'</a>',
'</h4>',
'<p><a href="',str(?verblijfsobject),'" target="_blank">Verblijfsobject</a>:</p>',
'<dl>',
  '<dt>Oppervlakte</dt><dd>',replace(str(?oppervlakte),'[0-9](?=(?:[0-9]{3})+(?![0-9]))','$0.'),' mÂ²</dd>',
  '<dt>Status</dt><dd><a href="',str(?verblijfsobjectStatus),'" target="_blank">',str(?verblijfsobjectStatusLabel),'</a></dd>',
'</dl>',
'<p><a href="',str(?gebouw),'" target="_blank">Gebouw</a>:</p>',
'<dl>',
  '<dt>Bouwjaar</dt><dd>',str(?bouwjaar),'</dd>',
  '<dt>Status</dt><dd><a href="',str(?gebouwStatus),'" target="_blank">',str(?gebouwStatusLabel),'</a></dd>',
'</dl>'),rdf:HTML) as ?puntLabel)
}
